import { Component } from '@angular/core';
import books from '../data/books';

@Component({
  selector: 'app-father',
  templateUrl: './father.component.html',
  styleUrls: ['./father.component.css']
})
export class FatherComponent {
  count: number = 0;
  Fathermessage: string = 'Message from Father: Hello Son!';

  localStorageObj: any = JSON.parse(localStorage.getItem('myobject') || '{}');
  recievedMessage: string = '';

  books = books;

  getBookClass(i: number): string {
    return i % 2 === 0 ? 'even-book' : 'odd-book';
  }

  receiveMessage($event: string) {
    this.recievedMessage = $event;
  }

  increment() {
    this.count++;
  }

  decrement() {
    if (this.count > 0) {
      this.count--;
    } else {
      this.count = 0;
    }
  }
}
<h2>father works!</h2>

<p>Message from son: {{ recievedMessage }}</p>

<div>
  <app-son
    (incrementFromSon)="increment()"
    (decrementFromSon)="decrement()"
    (messageEventFromSon)="receiveMessage($event)"
  ></app-son>

  <p>Counter: {{ count }}</p>
  <p>
    This information is coming from the local storage:
  </p>
  <!--<div class="book-content">
    <div *ngFor="let item of localStorageObj">
      <ul>
        <li *ngIf="item.book">Book: {{ item.book }}</li>
        <li *ngIf="item.author">Author: {{ item.author }}</li>
        <li *ngIf="item.year">Year: {{ item.year }}</li>
        <li *ngIf="item.pages">Pages: {{ item.pages }}</li>
      </ul>
    </div>
    [ngStyle]="{ 'background-color': i % 2 === 0 ? '#f0f0f0' : '#d0d0d0' }"
  </div> -->

  <div class="book-wrapper">
    <div
      *ngFor="let book of books; let i = index"
      [ngClass]="getBookClass(i)"
      class="book-content"
    >
      <div class="content">
        <p>Book: {{ book.book }}</p>
        <p>Author: {{ book.author }}</p>
        <p>Year: {{ book.year }}</p>
        <p>Pages: {{ book.pages }}</p>
        <div class="line"></div>
      </div>
    </div>
  </div>
</div>
